module.exports = {
   findprofanity: function(input) {
var bad_words=[
"nigger",
"niggers",
"niggle",
"niggles",
"fag",
"fags",
"faggot",
"faggots",
"tranny",
"chink",
"wetback",
"n1gger",
"n1ggers",
"n1gg3r",
"n1gg3rs",
"nigglet",
"nigglets",
"dyke",
"dykes"
];
   input=input.replace(/[\W_]+/g," ");
   var in_words=input.split(' ');
   var i=0,j=0;
   while (i < in_words.length)
   {
       j=0;
       while(j < bad_words.length)
       {
           if(in_words[i].toUpperCase()==bad_words[j].toUpperCase())
           {
              return true;
           }
           j=j+1;
        }
        i=i+1;
   }
   return false;
}
}
